        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721URIStorage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/PatriotPledgeNFTV3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract PatriotPledgeNFTV3 is ERC721, ERC721Enumerable, ERC721URIStorage, Ownable {
    uint256 private _nextTokenId;

    struct Campaign {
        string category;
        uint256 goal;
        uint256 grossRaised;
        uint256 netRaised;
        uint256 payoutEligible;
        uint256 payoutReleased;
        uint256 nonprofitFeeRate; // basis points, e.g. 100 = 1%
        address nonprofit; // custodial owner
        address submitter; // fundraiser/recipient
        bool closed;
    }

    mapping(uint256 => Campaign) public campaigns;

    event CampaignMinted(uint256 indexed tokenId, address indexed to, string uri, string category, uint256 goal, uint256 feeRate);
    event ContributionRecorded(uint256 indexed tokenId, uint256 gross, uint256 net, uint256 cardFees, uint256 nonprofitFee, bool isOnchain);
    event PayoutEligibleAdjusted(uint256 indexed tokenId, uint256 oldEligible, uint256 newEligible);
    event PayoutReleased(uint256 indexed tokenId, address indexed recipient, uint256 amount, bool onchain);
    event CampaignClosed(uint256 indexed tokenId);
    event WithdrawToCentral(address indexed recipient, uint256 amount);
    event CustodialTransfer(uint256 indexed tokenId, address indexed from, address indexed to);

    constructor() ERC721("Patriot Pledge NFT", "PPNFT") Ownable(msg.sender) {}

    receive() external payable {}

    fallback() external payable {}

    function mint(
        address to,
        string calldata uri,
        string calldata category,
        uint256 goal,
        uint256 feeRate
    ) external onlyOwner returns (uint256) {
        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);
        _setTokenURI(tokenId, uri);

        campaigns[tokenId] = Campaign({
            category: category,
            goal: goal,
            grossRaised: 0,
            netRaised: 0,
            payoutEligible: 0,
            payoutReleased: 0,
            nonprofitFeeRate: feeRate,
            nonprofit: to,
            submitter: address(0),
            closed: false
        });

        emit CampaignMinted(tokenId, to, uri, category, goal, feeRate);
        return tokenId;
    }

    function setSubmitter(uint256 tokenId, address submitter) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        c.submitter = submitter;
    }

    function recordContribution(
        uint256 tokenId,
        uint256 gross,
        uint256 net,
        uint256 cardFees,
        uint256 nonprofitFee,
        bool isOnchain
    ) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        require(!c.closed, "campaign closed");

        c.grossRaised += gross;
        c.netRaised += net;
        c.payoutEligible += (net - nonprofitFee);

        emit ContributionRecorded(tokenId, gross, net, cardFees, nonprofitFee, isOnchain);
    }

    function adjustPayoutEligible(uint256 tokenId, uint256 newEligible) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        uint256 old = c.payoutEligible;
        c.payoutEligible = newEligible;
        emit PayoutEligibleAdjusted(tokenId, old, newEligible);
    }

    function markPayoutReleased(
        uint256 tokenId,
        uint256 amount,
        address payable recipient,
        bool onchain
    ) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        require(amount <= c.payoutEligible - c.payoutReleased, "amount exceeds eligible");

        c.payoutReleased += amount;

        if (onchain) {
            require(address(this).balance >= amount, "insufficient contract balance");
            (bool ok, ) = recipient.call{value: amount}("");
            require(ok, "transfer failed");
        }

        emit PayoutReleased(tokenId, recipient, amount, onchain);
    }

    function closeCampaign(uint256 tokenId) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        c.closed = true;
        emit CampaignClosed(tokenId);
    }

    function withdrawToCentral(uint256 amount, address payable recipient) external onlyOwner {
        require(address(this).balance >= amount, "insufficient contract balance");
        (bool ok, ) = recipient.call{value: amount}("");
        require(ok, "withdraw failed");
        emit WithdrawToCentral(recipient, amount);
    }

    function transferNFT(uint256 tokenId, address newOwner) external onlyOwner {
        address from = ownerOf(tokenId);
        _update(newOwner, tokenId, from);
        emit CustodialTransfer(tokenId, from, newOwner);
    }

    function addRaised(uint256 tokenId, uint256 amount) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        c.grossRaised += amount;
        c.netRaised += amount;
        c.payoutEligible += amount;
    }

    function _update(address to, uint256 tokenId, address auth)
        internal
        override(ERC721, ERC721Enumerable)
        returns (address)
    {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._increaseBalance(account, value);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721URIStorage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/PatriotPledgeNFTV4.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title PatriotPledgeNFTV4
 * @notice Dynamic fundraising NFTs with on-chain tip tracking
 * @dev Tips are held on-chain; nonprofit has discretion to release to fundee
 */
contract PatriotPledgeNFTV4 is ERC721, ERC721Enumerable, ERC721URIStorage, Ownable {
    uint256 private _nextTokenId;

    struct Campaign {
        string category;           // "veteran" or "general"
        uint256 goal;              // Fundraising goal in wei/smallest unit
        uint256 grossRaised;       // Total raised before any fees
        uint256 netRaised;         // After card fees deducted
        uint256 payoutEligible;    // Amount eligible for payout to fundee
        uint256 payoutReleased;    // Amount already paid out to fundee
        uint256 nonprofitFeeRate;  // Basis points, e.g. 100 = 1%
        uint256 tipsReceived;      // Total tips received (held by nonprofit)
        uint256 tipsReleasedToFundee; // Tips released to fundee at nonprofit discretion
        address nonprofit;         // Custodial owner (nonprofit wallet)
        address submitter;         // Fundraiser/recipient
        bool closed;               // Campaign closed flag
    }

    mapping(uint256 => Campaign) public campaigns;

    // Events
    event CampaignMinted(
        uint256 indexed tokenId,
        address indexed to,
        string uri,
        string category,
        uint256 goal,
        uint256 feeRate
    );
    
    event ContributionRecorded(
        uint256 indexed tokenId,
        uint256 gross,
        uint256 net,
        uint256 cardFees,
        uint256 nonprofitFee,
        bool isOnchain
    );
    
    event TipRecorded(
        uint256 indexed tokenId,
        uint256 tipAmount,
        bool isOnchain
    );
    
    event TipReleasedToFundee(
        uint256 indexed tokenId,
        address indexed fundee,
        uint256 amount
    );
    
    event PayoutEligibleAdjusted(
        uint256 indexed tokenId,
        uint256 oldEligible,
        uint256 newEligible
    );
    
    event PayoutReleased(
        uint256 indexed tokenId,
        address indexed recipient,
        uint256 amount,
        bool onchain
    );
    
    event CampaignClosed(uint256 indexed tokenId);
    event WithdrawToCentral(address indexed recipient, uint256 amount);
    event CustodialTransfer(uint256 indexed tokenId, address indexed from, address indexed to);

    constructor() ERC721("Patriot Pledge NFT", "PPNFT") Ownable(msg.sender) {}

    // Allow contract to receive native tokens
    receive() external payable {}
    fallback() external payable {}

    /**
     * @notice Mint a new campaign NFT
     * @param to The nonprofit address that will hold the NFT
     * @param uri The metadata URI for the NFT
     * @param category "veteran" or "general"
     * @param goal The fundraising goal
     * @param feeRate Nonprofit fee in basis points (100 = 1%)
     */
    function mint(
        address to,
        string calldata uri,
        string calldata category,
        uint256 goal,
        uint256 feeRate
    ) external onlyOwner returns (uint256) {
        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);
        _setTokenURI(tokenId, uri);

        campaigns[tokenId] = Campaign({
            category: category,
            goal: goal,
            grossRaised: 0,
            netRaised: 0,
            payoutEligible: 0,
            payoutReleased: 0,
            nonprofitFeeRate: feeRate,
            tipsReceived: 0,
            tipsReleasedToFundee: 0,
            nonprofit: to,
            submitter: address(0),
            closed: false
        });

        emit CampaignMinted(tokenId, to, uri, category, goal, feeRate);
        return tokenId;
    }

    /**
     * @notice Set the submitter (fundee) address for a campaign
     */
    function setSubmitter(uint256 tokenId, address submitter) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        c.submitter = submitter;
    }

    /**
     * @notice Record a contribution (purchase) to a campaign
     * @param tokenId The campaign token ID
     * @param gross Gross amount before fees
     * @param net Net amount after card fees
     * @param cardFees Card processing fees deducted
     * @param nonprofitFee Fee going to nonprofit
     * @param isOnchain True if this was an on-chain crypto payment
     */
    function recordContribution(
        uint256 tokenId,
        uint256 gross,
        uint256 net,
        uint256 cardFees,
        uint256 nonprofitFee,
        bool isOnchain
    ) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        require(!c.closed, "Campaign closed");

        c.grossRaised += gross;
        c.netRaised += net;

        emit ContributionRecorded(tokenId, gross, net, cardFees, nonprofitFee, isOnchain);
    }

    /**
     * @notice Record a tip to a campaign (held by nonprofit)
     * @param tokenId The campaign token ID
     * @param tipAmount The tip amount
     * @param isOnchain True if this was an on-chain crypto payment
     */
    function recordTip(
        uint256 tokenId,
        uint256 tipAmount,
        bool isOnchain
    ) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        require(!c.closed, "Campaign closed");

        c.tipsReceived += tipAmount;
        // Tips also count toward gross raised for transparency
        c.grossRaised += tipAmount;

        emit TipRecorded(tokenId, tipAmount, isOnchain);
    }

    /**
     * @notice Record contribution AND tip in a single call (gas efficient)
     */
    function recordContributionWithTip(
        uint256 tokenId,
        uint256 gross,
        uint256 net,
        uint256 cardFees,
        uint256 nonprofitFee,
        uint256 tipAmount,
        bool isOnchain
    ) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        require(!c.closed, "Campaign closed");

        c.grossRaised += gross + tipAmount;
        c.netRaised += net;
        c.tipsReceived += tipAmount;

        emit ContributionRecorded(tokenId, gross, net, cardFees, nonprofitFee, isOnchain);
        if (tipAmount > 0) {
            emit TipRecorded(tokenId, tipAmount, isOnchain);
        }
    }

    /**
     * @notice Release tip to fundee (nonprofit discretion)
     * @param tokenId The campaign token ID
     * @param amount Amount of tips to release to fundee
     */
    function releaseTipToFundee(uint256 tokenId, uint256 amount) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        require(c.submitter != address(0), "No submitter set");
        require(amount <= c.tipsReceived - c.tipsReleasedToFundee, "Exceeds available tips");

        c.tipsReleasedToFundee += amount;

        emit TipReleasedToFundee(tokenId, c.submitter, amount);
    }

    /**
     * @notice Adjust payout eligible amount (for benchmark-based releases)
     */
    function adjustPayoutEligible(uint256 tokenId, uint256 newEligible) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        uint256 oldEligible = c.payoutEligible;
        c.payoutEligible = newEligible;

        emit PayoutEligibleAdjusted(tokenId, oldEligible, newEligible);
    }

    /**
     * @notice Release payout to fundee
     * @param tokenId The campaign token ID
     * @param amount Amount to release
     * @param onchain True if sending native tokens, false if off-chain transfer
     */
    function releasePayout(uint256 tokenId, uint256 amount, bool onchain) external onlyOwner {
        Campaign storage c = campaigns[tokenId];
        require(c.submitter != address(0), "No submitter set");
        require(amount <= c.payoutEligible - c.payoutReleased, "Exceeds eligible");

        c.payoutReleased += amount;

        if (onchain && amount > 0) {
            (bool sent, ) = c.submitter.call{value: amount}("");
            require(sent, "Transfer failed");
        }

        emit PayoutReleased(tokenId, c.submitter, amount, onchain);
    }

    /**
     * @notice Close a campaign (no more contributions)
     */
    function closeCampaign(uint256 tokenId) external onlyOwner {
        campaigns[tokenId].closed = true;
        emit CampaignClosed(tokenId);
    }

    /**
     * @notice Contribute native tokens (BDAG) to a campaign - PUBLIC PAYABLE
     * @param tokenId The campaign token ID to contribute to
     * @dev Anyone can call this to contribute. No card fees for crypto.
     */
    function contributeNative(uint256 tokenId) external payable {
        Campaign storage c = campaigns[tokenId];
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        require(!c.closed, "Campaign closed");
        require(msg.value > 0, "No value sent");
        
        // No card fees for crypto, just nonprofit fee
        uint256 gross = msg.value;
        uint256 nonprofitFee = (gross * c.nonprofitFeeRate) / 10000;
        uint256 net = gross - nonprofitFee;
        
        c.grossRaised += gross;
        c.netRaised += net;
        c.payoutEligible += net; // Crypto goes straight to eligible
        
        emit ContributionRecorded(tokenId, gross, net, 0, nonprofitFee, true);
    }

    /**
     * @notice Contribute native tokens with optional tip - PUBLIC PAYABLE
     * @param tokenId The campaign token ID
     * @param tipAmount Amount of msg.value that is a tip (rest is contribution)
     * @dev Tip is tracked separately and held by nonprofit
     */
    function contributeNativeWithTip(uint256 tokenId, uint256 tipAmount) external payable {
        Campaign storage c = campaigns[tokenId];
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        require(!c.closed, "Campaign closed");
        require(msg.value > 0, "No value sent");
        require(msg.value >= tipAmount, "Tip exceeds value");
        
        uint256 contribution = msg.value - tipAmount;
        uint256 nonprofitFee = contribution > 0 ? (contribution * c.nonprofitFeeRate) / 10000 : 0;
        uint256 net = contribution - nonprofitFee;
        
        c.grossRaised += msg.value; // Total includes tip
        c.netRaised += net;
        c.payoutEligible += net;
        c.tipsReceived += tipAmount;
        
        emit ContributionRecorded(tokenId, contribution, net, 0, nonprofitFee, true);
        if (tipAmount > 0) {
            emit TipRecorded(tokenId, tipAmount, true);
        }
    }

    /**
     * @notice Withdraw contract balance to central nonprofit wallet
     */
    function withdrawToCentral(address payable recipient, uint256 amount) external onlyOwner {
        require(amount <= address(this).balance, "Insufficient balance");
        (bool sent, ) = recipient.call{value: amount}("");
        require(sent, "Withdraw failed");

        emit WithdrawToCentral(recipient, amount);
    }

    /**
     * @notice Transfer custodial ownership of a campaign NFT
     */
    function custodialTransfer(uint256 tokenId, address newOwner) external onlyOwner {
        address oldOwner = ownerOf(tokenId);
        _transfer(oldOwner, newOwner, tokenId);
        campaigns[tokenId].nonprofit = newOwner;

        emit CustodialTransfer(tokenId, oldOwner, newOwner);
    }

    // View functions
    function getCampaign(uint256 tokenId) external view returns (Campaign memory) {
        return campaigns[tokenId];
    }

    function getAvailableTips(uint256 tokenId) external view returns (uint256) {
        Campaign storage c = campaigns[tokenId];
        return c.tipsReceived - c.tipsReleasedToFundee;
    }

    function getAvailablePayout(uint256 tokenId) external view returns (uint256) {
        Campaign storage c = campaigns[tokenId];
        return c.payoutEligible - c.payoutReleased;
    }

    function totalCampaigns() external view returns (uint256) {
        return _nextTokenId;
    }

    // Required overrides for multiple inheritance
    function _update(address to, uint256 tokenId, address auth)
        internal
        override(ERC721, ERC721Enumerable)
        returns (address)
    {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._increaseBalance(account, value);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721URIStorage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/PatriotPledgeNFTV5.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title PatriotPledgeNFTV5
 * @notice Edition-based fundraiser NFTs with living metadata updates
 * 
 * Flow:
 * 1. Admin creates a Campaign (no NFT minted yet)
 * 2. Donors purchase editions → NFT minted to donor's wallet
 * 3. Nonprofit can update campaign metadata (living NFT)
 * 4. All edition holders see updates via their NFT
 */
contract PatriotPledgeNFTV5 is ERC721, ERC721Enumerable, ERC721URIStorage, Ownable {
    uint256 private _nextTokenId;
    uint256 private _nextCampaignId;

    struct Campaign {
        string category;
        string baseURI;           // Base metadata URI (can be updated for living NFT)
        uint256 goal;             // Fundraising goal in smallest unit
        uint256 grossRaised;      // Total raised including tips
        uint256 netRaised;        // Net after fees
        uint256 tipsReceived;     // Tips collected
        uint256 editionsMinted;   // Number of editions minted
        uint256 maxEditions;      // Max editions (0 = unlimited)
        uint256 pricePerEdition;  // Price per edition in smallest unit
        uint256 nonprofitFeeRate; // Basis points (100 = 1%)
        address nonprofit;        // Nonprofit custodian
        address submitter;        // Original fundraiser/recipient
        bool active;              // Can still accept donations
        bool closed;              // Fully closed, no more activity
    }

    // Campaign ID => Campaign data
    mapping(uint256 => Campaign) public campaigns;
    
    // Token ID => Campaign ID (which campaign this edition belongs to)
    mapping(uint256 => uint256) public tokenToCampaign;
    
    // Token ID => Edition number within campaign
    mapping(uint256 => uint256) public tokenEditionNumber;
    
    // Campaign ID => list of token IDs (all editions)
    mapping(uint256 => uint256[]) public campaignEditions;

    // Events
    event CampaignCreated(
        uint256 indexed campaignId,
        address indexed nonprofit,
        string category,
        uint256 goal,
        uint256 maxEditions,
        uint256 pricePerEdition
    );
    
    event EditionMinted(
        uint256 indexed campaignId,
        uint256 indexed tokenId,
        address indexed donor,
        uint256 editionNumber,
        uint256 amountPaid
    );
    
    event CampaignMetadataUpdated(
        uint256 indexed campaignId,
        string newBaseURI
    );
    
    event ContributionRecorded(
        uint256 indexed campaignId,
        uint256 gross,
        uint256 net,
        uint256 tip,
        bool isOnchain
    );
    
    event CampaignClosed(uint256 indexed campaignId);

    constructor() ERC721("PatriotPledge Edition", "PPE") Ownable(msg.sender) {}

    // ============ Campaign Management ============

    /**
     * @notice Create a new fundraiser campaign (no NFT minted yet)
     * @param category Campaign category
     * @param baseURI Initial metadata URI
     * @param goal Fundraising goal
     * @param maxEditions Maximum editions (0 = unlimited)
     * @param pricePerEdition Price per edition
     * @param feeRate Nonprofit fee rate in basis points
     * @param submitter Address of the fundraiser/recipient
     */
    function createCampaign(
        string calldata category,
        string calldata baseURI,
        uint256 goal,
        uint256 maxEditions,
        uint256 pricePerEdition,
        uint256 feeRate,
        address submitter
    ) external onlyOwner returns (uint256) {
        uint256 campaignId = _nextCampaignId++;
        
        campaigns[campaignId] = Campaign({
            category: category,
            baseURI: baseURI,
            goal: goal,
            grossRaised: 0,
            netRaised: 0,
            tipsReceived: 0,
            editionsMinted: 0,
            maxEditions: maxEditions,
            pricePerEdition: pricePerEdition,
            nonprofitFeeRate: feeRate,
            nonprofit: msg.sender,
            submitter: submitter,
            active: true,
            closed: false
        });

        emit CampaignCreated(campaignId, msg.sender, category, goal, maxEditions, pricePerEdition);
        return campaignId;
    }

    /**
     * @notice Mint an edition NFT to a donor (called by backend after payment)
     * @param campaignId The campaign to mint from
     * @param donor Address to receive the NFT
     * @param amountPaid Amount the donor paid (for records)
     */
    function mintEditionToDonor(
        uint256 campaignId,
        address donor,
        uint256 amountPaid
    ) external onlyOwner returns (uint256) {
        Campaign storage c = campaigns[campaignId];
        require(c.active, "Campaign not active");
        require(!c.closed, "Campaign closed");
        require(c.maxEditions == 0 || c.editionsMinted < c.maxEditions, "Max editions reached");

        uint256 tokenId = _nextTokenId++;
        uint256 editionNumber = ++c.editionsMinted;
        
        _safeMint(donor, tokenId);
        _setTokenURI(tokenId, c.baseURI);
        
        tokenToCampaign[tokenId] = campaignId;
        tokenEditionNumber[tokenId] = editionNumber;
        campaignEditions[campaignId].push(tokenId);

        emit EditionMinted(campaignId, tokenId, donor, editionNumber, amountPaid);
        return tokenId;
    }

    /**
     * @notice Allow donors to mint directly with native BDAG
     * @param campaignId The campaign to contribute to
     */
    function mintWithBDAG(uint256 campaignId) external payable returns (uint256) {
        Campaign storage c = campaigns[campaignId];
        require(c.active, "Campaign not active");
        require(!c.closed, "Campaign closed");
        require(c.maxEditions == 0 || c.editionsMinted < c.maxEditions, "Max editions reached");
        require(msg.value >= c.pricePerEdition, "Insufficient payment");

        uint256 tokenId = _nextTokenId++;
        uint256 editionNumber = ++c.editionsMinted;
        
        // Update campaign financials
        c.grossRaised += msg.value;
        c.netRaised += msg.value; // Full amount for on-chain (no card fees)
        
        _safeMint(msg.sender, tokenId);
        _setTokenURI(tokenId, c.baseURI);
        
        tokenToCampaign[tokenId] = campaignId;
        tokenEditionNumber[tokenId] = editionNumber;
        campaignEditions[campaignId].push(tokenId);

        emit EditionMinted(campaignId, tokenId, msg.sender, editionNumber, msg.value);
        emit ContributionRecorded(campaignId, msg.value, msg.value, 0, true);
        
        return tokenId;
    }

    /**
     * @notice Mint with BDAG including a tip
     * @param campaignId The campaign to contribute to
     * @param tipAmount Amount of the payment that is a tip
     */
    function mintWithBDAGAndTip(uint256 campaignId, uint256 tipAmount) external payable returns (uint256) {
        Campaign storage c = campaigns[campaignId];
        require(c.active, "Campaign not active");
        require(!c.closed, "Campaign closed");
        require(c.maxEditions == 0 || c.editionsMinted < c.maxEditions, "Max editions reached");
        require(msg.value >= c.pricePerEdition + tipAmount, "Insufficient payment");

        uint256 tokenId = _nextTokenId++;
        uint256 editionNumber = ++c.editionsMinted;
        
        uint256 contribution = msg.value - tipAmount;
        
        // Update campaign financials
        c.grossRaised += msg.value;
        c.netRaised += contribution;
        c.tipsReceived += tipAmount;
        
        _safeMint(msg.sender, tokenId);
        _setTokenURI(tokenId, c.baseURI);
        
        tokenToCampaign[tokenId] = campaignId;
        tokenEditionNumber[tokenId] = editionNumber;
        campaignEditions[campaignId].push(tokenId);

        emit EditionMinted(campaignId, tokenId, msg.sender, editionNumber, msg.value);
        emit ContributionRecorded(campaignId, msg.value, contribution, tipAmount, true);
        
        return tokenId;
    }

    // ============ Living NFT - Metadata Updates ============

    /**
     * @notice Update campaign metadata (living NFT feature)
     * @dev Updates baseURI and refreshes all edition token URIs
     * @param campaignId Campaign to update
     * @param newBaseURI New metadata URI with progress updates
     */
    function updateCampaignMetadata(
        uint256 campaignId,
        string calldata newBaseURI
    ) external onlyOwner {
        Campaign storage c = campaigns[campaignId];
        require(!c.closed, "Campaign closed");
        
        c.baseURI = newBaseURI;
        
        // Update all edition token URIs
        uint256[] storage editions = campaignEditions[campaignId];
        for (uint256 i = 0; i < editions.length; i++) {
            _setTokenURI(editions[i], newBaseURI);
        }
        
        emit CampaignMetadataUpdated(campaignId, newBaseURI);
    }

    // ============ Financial Recording (for off-chain payments) ============

    /**
     * @notice Record a contribution made off-chain (card, PayPal, etc.)
     * @dev Called by backend after successful payment
     */
    function recordContribution(
        uint256 campaignId,
        uint256 gross,
        uint256 net,
        uint256 tip,
        bool isOnchain
    ) external onlyOwner {
        Campaign storage c = campaigns[campaignId];
        require(!c.closed, "Campaign closed");

        c.grossRaised += gross + tip;
        c.netRaised += net;
        c.tipsReceived += tip;

        emit ContributionRecorded(campaignId, gross, net, tip, isOnchain);
    }

    // ============ Campaign Lifecycle ============

    /**
     * @notice Deactivate campaign (no new editions, but not fully closed)
     */
    function deactivateCampaign(uint256 campaignId) external onlyOwner {
        campaigns[campaignId].active = false;
    }

    /**
     * @notice Reactivate a deactivated campaign
     */
    function reactivateCampaign(uint256 campaignId) external onlyOwner {
        Campaign storage c = campaigns[campaignId];
        require(!c.closed, "Campaign permanently closed");
        c.active = true;
    }

    /**
     * @notice Permanently close a campaign
     */
    function closeCampaign(uint256 campaignId) external onlyOwner {
        Campaign storage c = campaigns[campaignId];
        c.active = false;
        c.closed = true;
        emit CampaignClosed(campaignId);
    }

    // ============ View Functions ============

    /**
     * @notice Get campaign details
     */
    function getCampaign(uint256 campaignId) external view returns (
        string memory category,
        string memory baseURI,
        uint256 goal,
        uint256 grossRaised,
        uint256 netRaised,
        uint256 editionsMinted,
        uint256 maxEditions,
        uint256 pricePerEdition,
        bool active,
        bool closed
    ) {
        Campaign storage c = campaigns[campaignId];
        return (
            c.category,
            c.baseURI,
            c.goal,
            c.grossRaised,
            c.netRaised,
            c.editionsMinted,
            c.maxEditions,
            c.pricePerEdition,
            c.active,
            c.closed
        );
    }

    /**
     * @notice Get edition info for a token
     */
    function getEditionInfo(uint256 tokenId) external view returns (
        uint256 campaignId,
        uint256 editionNumber,
        uint256 totalEditions
    ) {
        campaignId = tokenToCampaign[tokenId];
        editionNumber = tokenEditionNumber[tokenId];
        totalEditions = campaigns[campaignId].editionsMinted;
    }

    /**
     * @notice Get all edition token IDs for a campaign
     */
    function getCampaignEditions(uint256 campaignId) external view returns (uint256[] memory) {
        return campaignEditions[campaignId];
    }

    /**
     * @notice Get total number of campaigns
     */
    function totalCampaigns() external view returns (uint256) {
        return _nextCampaignId;
    }

    // ============ Withdraw ============

    /**
     * @notice Withdraw collected BDAG to nonprofit
     */
    function withdraw(address payable to, uint256 amount) external onlyOwner {
        require(address(this).balance >= amount, "Insufficient balance");
        to.transfer(amount);
    }

    // Accept native tokens
    receive() external payable {}
    fallback() external payable {}

    // ============ Required Overrides ============

    function _update(address to, uint256 tokenId, address auth)
        internal
        override(ERC721, ERC721Enumerable)
        returns (address)
    {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._increaseBalance(account, value);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721URIStorage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

